import { Ionicons } from '@expo/vector-icons';
import React from 'react';
import { Image, TextInput, View,StyleSheet } from 'react-native';
import HeaderButtons from 'react-navigation-header-buttons';

//import Fire from '../Fire';

export default class NewPostScreen extends React.Component{
  static navigationOptions = ({ navigation }) => ({
    title: 'New Post',
    headerRight: (
      <HeaderButtons IconComponent={Ionicons} iconSize={23} color="black">
        <HeaderButtons.Item
          title="Share"
          onPress={() => {
            const text = navigation.getParam('text');
            const image = navigation.getParam('image');
            if (text && image) {
              navigation.goBack();
              Fire.shared.post({ text: text.trim(), image });
            } else {
              alert('Need valid description');
            }
          }}
        />
      </HeaderButtons>
    ),
  });

  state = { text: '' };

  render() {
    const { image } = this.props.route.params.image;
    return (
    <View>

        <View>
            <TextInput
                multiline
                placeholder="Write a Caption . . ."
                style={styles.input}
                onChangeText={(post_caption)=>{this.setState({post_caption})}}
            />
            </View>
        <Image source={{uri:`${this.props.route.params.image}`}} style={styles.myImage}/>
             <View style={styles.postContainer}>

          </View>

      </View>
    );
  }
}




const styles=StyleSheet.create({
  myImage:{
     height:400,
     width:"100%",
     borderBottomLeftRadius:10,
     borderBottomRightRadius:10,
     marginTop:100

  },
  input:{
      marginTop:100,
      paddingTop:20,
      fontSize:20,
      paddingLeft:10
  },

  myHeader:{
      width:"100%",
      paddingTop:40,
      paddingBottom:30,
      backgroundColor:"#fff"
  },


    postContainer:{
        display:"flex",
        alignItems:"center"
    }
})
