    Text,View,TouchableOpacity,
    StyleSheet,Image,TextInput

} from 'react-native';





class AddPost extends React.Component{

    constructor(props){
        super(props);
        this.state={
          photo:null,
          title:"",
          textinput:null,
          appear:true,

        }
      }



Disappear=()=>{
    this.setState({
      appear:!this.state.appear
    })

   }


    handleChoosePhoto=async()=>{
        const options={
            noData:true
        };



         let result=await ImagePicker.launchImageLibraryAsync({
              allowsEditing:true,
