import React from 'react';
import {Text,View,Button,TouchableOpacity} from 'react-native';
//import {createStackNavigator} from "react-navigation-stack";
//import {createAppContainer,NavigationContainer} from 'react-navigation'; 
import {NavigationContainer} from '@react-navigation/native';
import {createStackNavigator} from '@react-navigation/stack';
import AddPost from '../Screens/AddPost';
//import SelectImage from '../Screens/SelectPhotoScreen';
//import Save from '../Screens/NewPostScreen';
import SelectPhotoScreen from '../Screens/SelectPhotoScreen';
import NewPostScreen from '../Screens/NewPostScreen';
import MyContext from '../Screens/NewPostScreen';





const Stack = createStackNavigator();

function  AddStack(){
  const [create_post] = React.useContext(MyContext) 
  return (
    <Stack.Navigator

      initialRouteName="AddPost"
      headerMode="screen"

    >

    <Stack.Screen
        name="AddPost"
        component={AddPost}
        options={{
          title: 'Add Post',
        }}

      />

    <Stack.Screen
        name="SelectPhotoScreen"
        component={SelectPhotoScreen}
        options={{
          title: 'Select Photo',
        }}

      />

     <Stack.Screen
        name="NewPostScreen"
        component={NewPostScreen}
        options={{
          title: 'Discard',
          headerRight: (create_post) => (
         <TouchableOpacity style={{marginRight:10}} onPress={()=>create_post}>
           <Text style={{fontSize:18,fontWeight:"bold",}}>Share</Text>
         </TouchableOpacity>
          ),
        }}


      />



    </Stack.Navigator>
  );
}

export default AddStack;

